mapper.select:
select `id`, `activation_time`, `disabled_time`, `activation_ip`, `activation_ terminal`, `user_id`, `activation_code`, `is_activation`, `serve_last_time`, `serve_next_time`, `have_serve_ count`, `order_sn`, `serve_id` from nideshop_activation_card WHERE 1=1
AND name LIKE concat('%',#{name},'%')
order by ${sidx} ${order}
order by id desc
limit #{offset}, #{limit}

mapper.update:
update nideshop_activation_card
`activation_time` = #{activationTime}, `disabled_time` = #{disabledTime}, `activation_ip` = #{activationIp}, `activation_ terminal` = #{activation terminal}, `user_id` = #{userId}, `activation_code` = #{activationCode}, `is_activation` = #{isActivation}, `serve_last_time` = #{serveLastTime}, `serve_next_time` = #{serveNextTime}, `have_serve_ count` = #{haveServe count}, `order_sn` = #{orderSn}, `serve_id` = #{serveId}
where id = #{id}
